import React, { useEffect, useState } from 'react';

export default function CreateGroupModal({ open, onClose, friends = [], socket, token, onSuccess }) {
  const [groupName, setGroupName] = useState('');
  const [selected, setSelected] = useState(new Set());
  const [error, setError] = useState('');

  useEffect(() => {
    if (!open) {
      setGroupName('');
      setSelected(new Set());
      setError('');
    }
  }, [open]);

  if (!open) return null;

  const toggleSelect = (id) => {
    setSelected(prev => {
      const s = new Set(prev);
      if (s.has(id)) s.delete(id);
      else s.add(id);
      return s;
    });
  };

  const handleCreate = async () => {
    setError('');
    if (!groupName.trim()) {
      setError('Please enter a group name');
      return;
    }
    if (selected.size === 0) {
      setError('Please select at least one member');
      return;
    }

    const members = Array.from(selected);
    try {
      if (socket && socket.connected) {
        console.log('Creating group via socket:', { name: groupName, members });
        socket.emit('createGroup', { 
          name: groupName, 
          members, 
          avatar: '' 
        }, (response) => {
          if (response?.error) {
            setError(response.error);
          } else {
            onSuccess?.('Group created successfully!');
            onClose();
          }
        });
      } else {
        console.log('Creating group via HTTP');
        const res = await fetch('/api/groups', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`
          },
          body: JSON.stringify({ name: groupName, members })
        });
        const data = await res.json();
        console.log('Group creation response:', data);
      }
      onClose();
    } catch (err) {
      console.error('Create group error:', err);
      alert('Failed to create group: ' + err.message);
    }
  };

  return (
    <div className="popup-overlay" onClick={onClose}>
      <div className="popup-container" onClick={(e) => e.stopPropagation()}>
        <div className="popup-header">
          <h2 className="popup-title">Create Group</h2>
          <button className="popup-close" onClick={onClose} aria-label="Close">
            <svg viewBox="0 0 24 24" width="18" height="18" xmlns="http://www.w3.org/2000/svg">
              <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z" />
            </svg>
          </button>
        </div>

        <div className="popup-content">
          <div className="form-group">
            <label>Group name</label>
            <input
              className="form-input"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
              placeholder="Enter group name"
            />
          </div>

          <div style={{ marginTop: 12 }}>
            <label style={{ display: 'block', marginBottom: 8 }}>Select members</label>
            <div style={{ maxHeight: 260, overflowY: 'auto', border: '1px solid #f0f0f0', borderRadius: 8 }}>
              {friends.length === 0 && (
                <div style={{ padding: 12, color: '#666' }}>No friends to add</div>
              )}
              {friends.map(f => (
                <div key={f.id} style={{ display: 'flex', alignItems: 'center', gap: 12, padding: 10, borderBottom: '1px solid #f6f6f6' }}>
                  <input type="checkbox" checked={selected.has(f.id)} onChange={() => toggleSelect(f.id)} />
                  <div style={{ display: 'flex', flexDirection: 'column' }}>
                    <div style={{ fontWeight: 600 }}>{f.name}</div>
                    <div style={{ fontSize: 12, color: '#666' }}>{f.email}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: 16 }}>
            <button className="send-request-btn" onClick={handleCreate} disabled={!groupName.trim()}>
              Create
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
