import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import AddFriendModal from '../Components/AddFriendModal';
import FriendRequestsModal from '../Components/FriendRequestsModal';
import CreateGroupModal from '../Components/CreateGroupModal';
import '../Styles/Main.css';

const Main = () => {
  const navigate = useNavigate();
  const [isAddFriendModalOpen, setIsAddFriendModalOpen] = useState(false);
  const [isRequestsModalOpen, setIsRequestsModalOpen] = useState(false);
  
  const [socket, setSocket] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [friends, setFriends] = useState([]);
  const [pendingRequests, setPendingRequests] = useState([]);
  const [selectedFriend, setSelectedFriend] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);
  const selectedFriendRef = useRef(null);
  const [loading, setLoading] = useState(false);
  const [friendRequests, setFriendRequests] = useState([]);
  const [myFriends, setMyFriends] = useState([]);
  const [sidebarTab, setSidebarTab] = useState('requests'); // 'requests' or 'friends'
  const [activeTab, setActiveTab] = useState('requests'); // For popup: 'requests' or 'add'
  const [searchQuery, setSearchQuery] = useState('');
  const [friendEmail, setFriendEmail] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [showAddFriendPopup, setShowAddFriendPopup] = useState(false);
  const [showCreateGroup, setShowCreateGroup] = useState(false);
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [isGroupChat, setIsGroupChat] = useState(false);
  const [groupInvites, setGroupInvites] = useState([]);

  // Function to load chat history
  const loadChatHistory = async (friendId) => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const res = await fetch(`/api/chat/${friendId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await res.json();
      if (res.ok) {
        // Filter messages to only show those between current user and selected friend
        const filteredMessages = data.messages.filter(msg => 
          (msg.sender._id === friendId && msg.recipient._id === currentUser?.id) ||
          (msg.sender._id === currentUser?.id && msg.recipient._id === friendId)
        );
        setMessages(filteredMessages);
      } else {
        console.error('Failed to load messages:', data);
      }
    } catch (error) {
      console.error('Error loading messages:', error);
    } finally {
      setLoading(false);
    }
  };

  // Function to send a message (supports group or direct)
  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;
    try {
      // If group chat, send via socket
      if (isGroupChat && selectedGroup) {
        if (socket && socket.connected) {
          console.log('Sending group message:', {
            groupId: selectedGroup._id || selectedGroup.id,
            content: newMessage
          });
          socket.emit('groupMessage', { 
            groupId: selectedGroup._id || selectedGroup.id, 
            content: newMessage 
          });
          // optimistic UI update
          setMessages(prev => [...prev, {
            _id: Date.now(),
            sender: { _id: currentUser?.id, name: currentUser?.name },
            content: newMessage,
            timestamp: new Date().toISOString(),
            isGroupMessage: true
          }]);
          setNewMessage('');
        }
        return;
      }

      // Direct message
      if (!selectedFriend) return;
      const token = localStorage.getItem('token');
      const res = await fetch(`/api/chat/${selectedFriend.id}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ content: newMessage })
      });

      const data = await res.json();
      if (res.ok) {
        setMessages(prev => [...prev, data.message]);
        setNewMessage('');
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  // Scroll to bottom when new messages arrive
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollTop = messagesEndRef.current.scrollHeight;
    }
  }, [messages]);

  // Load chat history when selecting a friend
  useEffect(() => {
    if (selectedFriend) {
      loadChatHistory(selectedFriend.id);
    }
    // keep a ref copy so websocket handler can read latest selectedFriend without reattaching
    selectedFriendRef.current = selectedFriend;
  }, [selectedFriend]);

  // Initialize WebSocket once on mount and handle incoming messages
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.log('No token found, redirecting to signin');
      navigate('/signin');
      return;
    }

    console.log('Setting up WebSocket connection...');
    const newSocket = io('http://localhost:5000', { 
      auth: { token },
      transports: ['websocket'],
      reconnection: true
    });
    setSocket(newSocket);

    newSocket.on('connect', () => {
      console.log('Socket connected successfully, ID:', newSocket.id);
      console.log('Authenticating socket with token...');
      newSocket.emit('authenticate', token);
    });

    newSocket.on('connect_error', (error) => {
      console.error('Socket connection error:', error);
    });

    newSocket.on('error', (error) => {
      console.error('Socket error:', error);
    });

    // support server's 'newMessage' event (emitted on send) and legacy 'message'
    const handleIncoming = (data) => {
      // data expected to contain: senderId, recipientId, content, timestamp
      const sf = selectedFriendRef.current;
      
      // For direct messages, only add if it belongs to the current chat
      if (!data.isGroupMessage) {
        const belongsToOpenChat = sf && (
          String(data.sender?._id || data.senderId) === String(sf.id) || 
          String(data.recipient?._id || data.recipientId) === String(sf.id)
        );
        
        if (belongsToOpenChat) {
          setMessages(prev => [...prev, {
            _id: data.id || data._id || Date.now(),
            sender: { _id: data.sender?._id || data.senderId },
            recipient: { _id: data.recipient?._id || data.recipientId },
            content: data.content,
            timestamp: data.timestamp || new Date().toISOString()
          }]);
        }
      } else {
        // For group messages, only add if current group is selected
        if (selectedGroup && data.groupId === selectedGroup._id) {
          setMessages(prev => [...prev, {
            _id: data.id || data._id || Date.now(),
            sender: { 
              _id: data.sender?._id || data.senderId,
              name: data.sender?.name
            },
            content: data.content,
            timestamp: data.timestamp || new Date().toISOString(),
            isGroupMessage: true,
            groupId: data.groupId
          }]);
        }
      }

      // Optionally, if message not for open chat, you can show a notification badge here
      if (!belongsToOpenChat) {
        // TODO: show unread indicator on friend list
      }
    };

    newSocket.on('newMessage', handleIncoming);
    newSocket.on('message', handleIncoming);

    newSocket.on('friendRequest', (data) => {
      setFriendRequests(prev => [
        {
          id: data.id,
          name: data.sender.name,
          email: data.sender.email,
          avatar: null
        },
        ...prev
      ]);
    });

    // Group-related socket events
    newSocket.on('groupCreated', (group) => {
      console.log('Group created event received:', group);
      if (group) {
        setGroups(prev => [group, ...prev]);
        setAlertMessage('Group created successfully!');
        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 3000);
        
        // auto-join the group room for the creator
        const groupId = group._id || group.id;
        console.log('Auto-joining group room:', groupId);
        newSocket.emit('joinGroup', groupId);
      }
    });

    newSocket.on('groupInvite', ({ group }) => {
      console.log('Group invite received:', group);
      // Update group invites list
      setGroupInvites(prev => [...prev, group]);
      // Show notification
      setAlertMessage(`You were invited to group: ${group.name}`);
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 3000);
    });

    newSocket.on('groupMessage', (msg) => {
      // If currently viewing this group, append; otherwise show toast (simple approach)
      if (selectedGroup && (String(selectedGroup._id || selectedGroup.id) === String(msg.group || msg.groupId || msg.group._id))) {
        setMessages(prev => [...prev, msg]);
      } else {
        setAlertMessage('New group message');
        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 2000);
      }
    });

    newSocket.on('groupMemberJoined', ({ groupId, userId }) => {
      // optional: update group members in state
      setGroups(prev => prev.map(g => (String(g._id) === String(groupId) ? { ...g, members: [...(g.members||[]), userId] } : g)));
    });

    newSocket.on('groupTitleChanged', ({ groupId, title }) => {
      setGroups(prev => prev.map(g => (String(g._id) === String(groupId) ? { ...g, name: title } : g)));
    });

    return () => {
      if (newSocket) newSocket.close();
    };
  }, [navigate]);

  // Fetch initial friend requests and show welcome alert
  useEffect(() => {
    const fetchFriendRequests = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/signin');
          return;
        }

        const res = await fetch('/api/friends/requests', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        const data = await res.json();

        if (res.ok && data.requests) {
          setFriendRequests(data.requests.map(req => ({
            id: req._id,
            name: req.sender.name,
            email: req.sender.email,
            avatar: null
          })));
        }
      } catch (error) {
        console.error('Fetch friend requests error:', error);
      }
    };

    fetchFriendRequests();
    setAlertMessage('Welcome to Chat App!');
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
    }, 2000);
    // Also fetch current friends list
    const fetchFriends = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        const res = await fetch('/api/friends/list', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const data = await res.json();
        if (res.ok && data.friends) {
          const friendsList = data.friends.map(f => ({ id: f._id || f.id, name: f.name, email: f.email, avatar: null }));
          setMyFriends(friendsList);
          // If there are friends, show the friends tab and auto-select the first friend
          if (friendsList.length > 0) {
            setSidebarTab('friends');
            // auto-select the first friend to show conversation in main area
            setSelectedFriend(friendsList[0]);
            // optionally load chat history for the selected friend
            loadChatHistory(friendsList[0].id);
          }
        }
      } catch (err) {
        console.error('Error fetching friends list:', err);
      }
    };

      const fetchGroups = async () => {
        try {
          const token = localStorage.getItem('token');
          if (!token) return;
          console.log('Fetching groups...');
          const res = await fetch('/api/groups', { 
            headers: { 
              Authorization: `Bearer ${token}` 
            } 
          });
          const data = await res.json();
          console.log('Groups response:', data);
          if (res.ok && data.groups) {
            setGroups(data.groups);
            console.log('Groups set:', data.groups);
          }
        } catch (err) {
          console.error('Error fetching groups:', err);
        }
      };

      const fetchGroupInvites = async () => {
        try {
          const token = localStorage.getItem('token');
          if (!token) return;
          const res = await fetch('/api/groups/invites', { headers: { Authorization: `Bearer ${token}` } });
          const data = await res.json();
          if (res.ok && data.invites) {
            setGroupInvites(data.invites);
          }
        } catch (err) {
          console.error('Error fetching group invites:', err);
        }
      };

    fetchFriends();
    fetchGroups();
    fetchGroupInvites();
  }, [navigate]);

  const loadGroupMessages = async (groupId) => {
    try {
      setLoading(true);
      console.log('Loading messages for group:', groupId);
      const token = localStorage.getItem('token');
      const res = await fetch(`/api/groups/${groupId}/messages`, { 
        headers: { 
          Authorization: `Bearer ${token}` 
        } 
      });
      const data = await res.json();
      console.log('Group messages response:', data);
      if (res.ok) {
        // Only include messages that belong to this specific group
        const formattedMessages = (data.messages || data)
          .filter(msg => msg.groupId === groupId)
          .map(msg => ({
            _id: msg._id,
            sender: {
              _id: msg.sender._id || msg.sender,
              name: msg.sender.name
            },
            content: msg.content,
            timestamp: msg.timestamp || msg.createdAt,
            isGroupMessage: true,
            groupId: groupId
          }));
        setMessages(formattedMessages);
      }
    } catch (err) {
      console.error('Error fetching group messages:', err);
      setAlertMessage('Failed to load group messages');
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 3000);
    } finally {
      setLoading(false);
    }
  };

  const handleLeave = () => {
    navigate('/signin');
  };
  // User profile from localStorage (set on signin/signup)
  const [currentUser, setCurrentUser] = useState(() => {
    try {
      const u = localStorage.getItem('user');
      return u ? JSON.parse(u) : null;
    } catch (e) {
      return null;
    }
  });

  const [showProfileMenu, setShowProfileMenu] = useState(false);

  const handleLogout = () => {
    // clear auth and redirect to signin
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setCurrentUser(null);
    navigate('/signin');
  };

  const handleAcceptRequest = async (requestId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/signin');
        return;
      }

      const res = await fetch(`/api/friends/accept/${requestId}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await res.json();

      if (!res.ok) {
        setAlertMessage(data.message || 'Failed to accept friend request');
        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 2000);
        return;
      }

      // Move friend from requests to friends list
      const friend = friendRequests.find(f => f.id === requestId);
      if (friend) {
        setMyFriends([...myFriends, friend]);
        setFriendRequests(friendRequests.filter(f => f.id !== requestId));
        setAlertMessage(`You are now friends with ${friend.name}!`);
        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 2000);
      }
    } catch (error) {
      console.error('Accept friend request error:', error);
      setAlertMessage('Network error while accepting friend request');
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 2000);
    }
  };

  const handleDeclineRequest = (friendId) => {
    setFriendRequests(friendRequests.filter(f => f.id !== friendId));
    setAlertMessage('Friend request declined.');
    setShowAlert(true);
    setTimeout(() => setShowAlert(false), 2000);
  };

  const handleSendFriendRequest = async () => {
    if (!friendEmail || !isValidEmail(friendEmail)) return;
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/signin');
        return;
      }

      const res = await fetch('/api/friends/request', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ recipientEmail: friendEmail })
      });

      const data = await res.json();
      if (!res.ok) {
        setAlertMessage(data.message || 'Failed to send friend request');
        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 2000);
        return;
      }

      setAlertMessage('Friend request sent successfully!');
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 2000);
      setFriendEmail('');
    } catch (error) {
      console.error('Send friend request error:', error);
      setAlertMessage('Network error while sending friend request');
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 2000);
    }
  };

  const isValidEmail = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  const filteredRequests = friendRequests.filter(friend =>
    friend.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    friend.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const filteredFriends = myFriends.filter(friend =>
    friend.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    friend.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="main-container">
      {/* Left Sidebar */}
      <div className="sidebar">
          <div className="sidebar-header">
          <h1 className="sidebar-title">Messages</h1>
          <div className="sidebar-actions">
            <button 
              className="action-btn"
              onClick={() => setShowAddFriendPopup(true)}
              title="Add Friend"
            >
              <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path d="M20 4H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 14H4V6h16v12z"/>
                <path d="M12 11.5c.8 0 1.5-.7 1.5-1.5s-.7-1.5-1.5-1.5-1.5.7-1.5 1.5.7 1.5 1.5 1.5z"/>
                <path d="M7 10h2v2H7zm8 0h2v2h-2z"/>
                <circle cx="12" cy="12" r="1.5"/>
              </svg>
            </button>
            {/* Notification badge for friend requests and group invites */}
            {(friendRequests.length > 0 || groupInvites.length > 0) && (
              <span className="notification-badge">
                {friendRequests.length + groupInvites.length}
              </span>
            )}
            <button
              className="action-btn"
              onClick={() => setShowCreateGroup(true)}
              title="Create Group"
              style={{ marginLeft: 8 }}
            >
              <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" width="18" height="18">
                <path d="M12 2a3 3 0 0 1 3 3v1h3a1 1 0 0 1 1 1v2H5V7a1 1 0 0 1 1-1h3V5a3 3 0 0 1 3-3zM6 12v6a2 2 0 0 0 2 2h8a2 2 0 0 0 2-2v-6H6z" fill="currentColor"/>
              </svg>
            </button>
            {/* Profile avatar button (replaces previous Leave/logout button) */}
            <div style={{ position: 'relative' }}>
              <button
                className="action-btn"
                onClick={(e) => { e.stopPropagation(); setShowProfileMenu(!showProfileMenu); }}
                title={currentUser ? currentUser.name : 'Profile'}
              >
                <div style={{ width: 28, height: 28, borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#7B42F6', color: '#fff', fontWeight: '600' }}>
                  {currentUser && currentUser.name ? currentUser.name.charAt(0).toUpperCase() : 'U'}
                </div>
              </button>

              {showProfileMenu && (
                <div
                  className="profile-menu"
                  onClick={(e) => e.stopPropagation()}
                  style={{
                    position: 'absolute',
                    right: 0,
                    top: '40px',
                    background: '#fff',
                    boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                    borderRadius: 8,
                    padding: 12,
                    minWidth: 220,
                    zIndex: 50
                  }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
                    <div style={{ width: 48, height: 48, borderRadius: '50%', background: '#7B42F6', color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 18 }}>
                      {currentUser && currentUser.name ? currentUser.name.charAt(0).toUpperCase() : 'U'}
                    </div>
                    <div>
                      <div style={{ fontWeight: 600 }}>{currentUser ? currentUser.name : 'User'}</div>
                      <div style={{ fontSize: 12, color: '#666' }}>{currentUser ? currentUser.email : 'no-email'}</div>
                    </div>
                  </div>

                  <div style={{ height: 1, background: '#eee', margin: '12px 0' }} />

                  <button
                    className="logout-btn"
                    onClick={() => handleLogout()}
                    style={{
                      width: '100%',
                      padding: '8px 10px',
                      borderRadius: 6,
                      border: 'none',
                      background: '#ef4444',
                      color: '#fff',
                      cursor: 'pointer'
                    }}
                  >
                    Logout
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="search-container">
          <div className="search-box">
            <svg className="search-icon" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0 0 16 9.5 6.5 6.5 0 1 0 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"/>
            </svg>
            <input
              type="text"
              className="search-input"
              placeholder="Search conversations..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>

        <div className="conversations-container">
          {sidebarTab === 'requests' && friendRequests.length > 0 && (
            <>
              {filteredRequests.map(friend => (
                <div key={friend.id} className="conversation-item request-item">
                  <div className="conversation-avatar">
                    {friend.avatar ? (
                      <img src={friend.avatar} alt={friend.name} />
                    ) : (
                      <div className="avatar-placeholder">
                        {friend.name.charAt(0).toUpperCase()}
                      </div>
                    )}
                    <span className="status-badge">New</span>
                  </div>
                  <div className="conversation-info">
                    <h3 className="conversation-name">{friend.name}</h3>
                    <p className="conversation-email">{friend.email}</p>
                  </div>
                  <div className="request-actions">
                    <button 
                      className="accept-btn"
                      onClick={() => handleAcceptRequest(friend.id)}
                      title="Accept"
                    >
                      <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path d="M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z"/>
                      </svg>
                    </button>
                    <button 
                      className="decline-btn"
                      onClick={() => handleDeclineRequest(friend.id)}
                      title="Decline"
                    >
                      <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
                      </svg>
                    </button>
                  </div>
                </div>
              ))}
            </>
          )}

          {/* Group Invites Section */}
          {sidebarTab === 'requests' && groupInvites.length > 0 && (
            <div className="section-header">
              <h3>Group Invites ({groupInvites.length})</h3>
              {groupInvites.map(invite => (
                <div key={invite._id || invite.id} className="request-item">
                  <div className="avatar-container">
                    {invite.avatar ? (
                      <img src={invite.avatar} alt={invite.name} className="avatar" />
                    ) : (
                      <div className="avatar-placeholder">
                        {invite.name.charAt(0).toUpperCase()}
                      </div>
                    )}
                  </div>
                  <div className="request-info">
                    <h3>{invite.name}</h3>
                    <p>Group Invite</p>
                  </div>
                  <div className="request-buttons">
                    <button
                      className="btn-accept"
                      onClick={() => {
                        if (socket && socket.connected) {
                          console.log('Accepting group invite:', invite._id || invite.id);
                          socket.emit('acceptGroupInvite', { groupId: invite._id || invite.id });
                          setGroupInvites(prev => prev.filter(g => g._id !== invite._id && g.id !== invite.id));
                          setGroups(prev => [...prev, invite]);
                          setAlertMessage('Joined group: ' + invite.name);
                          setShowAlert(true);
                          setTimeout(() => setShowAlert(false), 3000);
                        }
                      }}
                    >
                      Join
                    </button>
                    <button
                      className="btn-decline"
                      onClick={() => {
                        console.log('Declining group invite:', invite._id || invite.id);
                        setGroupInvites(prev => prev.filter(g => g._id !== invite._id && g.id !== invite.id));
                      }}
                    >
                      Decline
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Friend Requests Section */}
          {sidebarTab === 'requests' && friendRequests.length > 0 && (
            <div className="section-header">
              <h3>Friend Requests ({friendRequests.length})</h3>
            </div>
          )}

          {/* Groups Section */}
          {sidebarTab === 'friends' && groups.length > 0 && (
            <div className="section-header">
              <h3>Groups ({groups.length})</h3>
              <div className="groups-list">
                {groups.map(group => (
                  <div
                    key={group._id || group.id}
                    className={`conversation-item ${selectedGroup?._id === group._id ? 'active' : ''}`}
                    onClick={() => {
                      setSelectedGroup(group);
                      setSelectedFriend(null);
                      setIsGroupChat(true);
                      // Join group room if not already joined
                      if (socket && socket.connected) {
                        socket.emit('joinGroup', group._id || group.id);
                      }
                    }}
                  >
                    <div className="conversation-avatar">
                      {group.avatar ? (
                        <img src={group.avatar} alt={group.name} className="group-avatar" />
                      ) : (
                        <div className="avatar-placeholder">
                          {group.name.charAt(0).toUpperCase()}
                        </div>
                      )}
                    </div>
                    <div className="conversation-info">
                      <h3 className="conversation-name">{group.name}</h3>
                      <p className="conversation-meta">{group.members?.length || 0} members</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Friends Section */}
          {sidebarTab === 'friends' && myFriends.length > 0 && (
            <>
              <div className="section-header">
                <h3>Friends ({myFriends.length})</h3>
              </div>
              {filteredFriends.map(friend => (
                <div
                  key={friend.id}
                  className={`conversation-item ${selectedFriend?.id === friend.id ? 'active' : ''}`}
                  onClick={() => {
                    setSelectedFriend(friend);
                    setSelectedGroup(null);
                    setIsGroupChat(false);
                  }}
                >
                  <div className="conversation-avatar">
                    {friend.avatar ? (
                      <img src={friend.avatar} alt={friend.name} />
                    ) : (
                      <div className="avatar-placeholder">
                        {friend.name.charAt(0).toUpperCase()}
                      </div>
                    )}
                  </div>
                  <div className="conversation-info">
                    <h3 className="conversation-name">{friend.name}</h3>
                    <p className="conversation-email">{friend.email}</p>
                  </div>
                </div>
              ))}
            </>
          )}

          {(!friendRequests.length && sidebarTab === 'requests') && (
            <div className="empty-state">
              <p className="empty-state-title">No conversations yet</p>
              <p className="empty-state-subtitle">Start a new chat to get going</p>
            </div>
          )}

          {(!myFriends.length && sidebarTab === 'friends') && (
            <div className="empty-state">
              <p className="empty-state-title">No friends yet</p>
              <p className="empty-state-subtitle">Accept friend requests to chat</p>
            </div>
          )}
        </div>
      </div>

      {/* Right Main Area */}
      <div className="main-content">
        {(selectedFriend || selectedGroup) ? (
          <div className="chat-container">
            <div className="chat-header">
              <div className="chat-user-info">
                {isGroupChat ? (
                  // Group chat header
                  <>
                    <div className="chat-avatar-placeholder group-avatar">
                      {selectedGroup.name.charAt(0).toUpperCase()}
                    </div>
                    <div className="chat-user-details">
                      <h3>{selectedGroup.name}</h3>
                      <p>{selectedGroup.members?.length || 0} members</p>
                    </div>
                  </>
                ) : (
                  // Direct chat header
                  <>
                    {selectedFriend.avatar ? (
                      <img src={selectedFriend.avatar} alt={selectedFriend.name} className="chat-avatar" />
                    ) : (
                      <div className="chat-avatar-placeholder">
                        {selectedFriend.name.charAt(0).toUpperCase()}
                      </div>
                    )}
                    <div className="chat-user-details">
                      <h3>{selectedFriend.name}</h3>
                      <p>{selectedFriend.email}</p>
                    </div>
                  </>
                )}
              </div>
            </div>

            <div className="messages-container" ref={messagesEndRef}>
              {(() => {
                if (loading) {
                  return <div className="loading-messages">Loading messages...</div>;
                }
                
                if (messages.length === 0) {
                  return (
                    <div className="no-messages">
                      <p>No messages yet</p>
                      <p>Start the conversation!</p>
                    </div>
                  );
                }
                
                return messages.map((msg) => {
                  const isSentByMe = msg.sender._id === (currentUser && currentUser.id);
                  const senderName = isGroupChat && !isSentByMe ? 
                    selectedGroup.members.find(m => m._id === msg.sender._id)?.name || 'Unknown' : '';

                  return (
                    <div
                      key={msg._id}
                      className={`message ${isSentByMe ? 'sent' : 'received'}`}
                    >
                      {isGroupChat && !isSentByMe && (
                        <div className="message-sender">{senderName}</div>
                      )}
                      <div className="message-content">
                        <p>{msg.content}</p>
                        <div className="message-time">
                          {new Date(msg.timestamp).toLocaleTimeString([], {
                            hour: '2-digit',
                            minute: '2-digit'
                          })}
                          {isSentByMe && (
                            <span className="tick" aria-hidden>
                              <svg viewBox="0 0 24 24" width="16" height="16" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <path d="M1 13.5L5.5 18L11.5 6" stroke="#ffffff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" opacity="0.9"/>
                                <path d="M7 13.5L11.5 18L19 6" stroke="#ffffff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" opacity="0.9"/>
                              </svg>
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                  );
                });
              })()}
            </div>

            <div className="message-input-container">
              <input
                type="text"
                className="message-input"
                placeholder="Type a message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
              />
              <button 
                className="send-message-btn"
                onClick={handleSendMessage}
                disabled={!newMessage.trim()}
              >
                <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
                </svg>
              </button>
            </div>
          </div>
        ) : (
          <div className="empty-chat">
            <p className="empty-chat-title">Select a chat to start messaging</p>
            <p className="empty-chat-subtitle">or create a new conversation</p>
          </div>
        )}
      </div>

      {/* Add Friend Popup */}
      {showAddFriendPopup && (
        <div className="popup-overlay" onClick={() => setShowAddFriendPopup(false)}>
          <div className="popup-container" onClick={(e) => e.stopPropagation()}>
            <div className="popup-header">
              <h2 className="popup-title">Add Friend</h2>
              <button 
                className="popup-close"
                onClick={() => setShowAddFriendPopup(false)}
              >
                <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
                </svg>
              </button>
            </div>

            <div className="popup-tabs">
              <button
                className={`popup-tab ${activeTab === 'requests' ? 'active' : ''}`}
                onClick={() => setActiveTab('requests')}
              >
                Friend Requests ({friendRequests.length})
              </button>
              <button
                className={`popup-tab ${activeTab === 'add' ? 'active' : ''}`}
                onClick={() => setActiveTab('add')}
              >
                My Friends ({myFriends.length})
              </button>
            </div>

            <div className="popup-content">
              {activeTab === 'add' && (
                <div className="add-friend-form">
                  <div className="form-group">
                    <label htmlFor="friend-email">Friend's Email</label>
                    <div className="input-with-button">
                      <input
                        type="email"
                        id="friend-email"
                        className="form-input"
                        placeholder="Enter email address"
                        value={friendEmail}
                        onChange={(e) => setFriendEmail(e.target.value)}
                      />
                      <button
                        className="send-request-btn"
                        onClick={handleSendFriendRequest}
                        disabled={!friendEmail || !isValidEmail(friendEmail)}
                      >
                        Send Request
                      </button>
                    </div>
                  </div>
                </div>
              )}

              {activeTab === 'requests' && (
                <div className="requests-list">
                  {friendRequests.map(friend => (
                    <div key={friend.id} className="request-item-popup">
                      <div className="request-avatar">
                        {friend.avatar ? (
                          <img src={friend.avatar} alt={friend.name} />
                        ) : (
                          <div className="avatar-placeholder">
                            {friend.name.charAt(0).toUpperCase()}
                          </div>
                        )}
                      </div>
                      <div className="request-info">
                        <h3>{friend.name}</h3>
                        <p>{friend.email}</p>
                      </div>
                      <div className="request-buttons">
                        <button
                          className="btn-accept"
                          onClick={() => {
                            handleAcceptRequest(friend.id);
                            if (friendRequests.length === 1) setActiveTab('add');
                          }}
                        >
                          Accept
                        </button>
                        <button
                          className="btn-decline"
                          onClick={() => handleDeclineRequest(friend.id)}
                        >
                          Decline
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Create Group Modal */}
      {showCreateGroup && (
        <CreateGroupModal 
          open={showCreateGroup} 
          onClose={() => setShowCreateGroup(false)}
          friends={myFriends}
          socket={socket}
          token={localStorage.getItem('token')}
          onSuccess={(message) => {
            setAlertMessage(message);
            setShowAlert(true);
            setTimeout(() => setShowAlert(false), 3000);
          }}
        />
      )}

      {/* Alert Box */}
      {showAlert && (
        <div className="alert-box">
          <p>{alertMessage}</p>
        </div>
      )}
    </div>
  );
};

export default Main;
